create table TOURPLAN
(
    PLANID    NUMBER        not null
        primary key,
    USERID    NUMBER
        constraint FK_PLAN_USER
            references TOURUSER,
    REGION    VARCHAR2(45)  not null,
    STARTDATE DATE          not null,
    ENDDATE   DATE          not null,
    PLANLIST  VARCHAR2(100) not null,
    PLANSIZE  VARCHAR2(45)  not null,
    PLANDATE  DATE          not null,
    PLANTITLE VARCHAR2(200) not null
)
/

